document.addEventListener("DOMContentLoaded",(function(){fetch("/assets/blog/posts.json").then((t=>t.json())).then((t=>{t.sort(((t,e)=>e.id-t.id));const e=document.getElementById("blog-list");t.forEach((t=>{const n=document.createElement("article");n.classList.add("relative","overflow-hidden","rounded-lg","shadow","transition","hover:shadow-lg"),n.innerHTML=`\n                    <img\n                        alt="${t.title}"\n                        src="${t.thumbnail}"\n                        class="absolute inset-0 h-full w-full object-cover"\n                    />\n                    <div class="relative bg-gradient-to-t from-gray-900/50 to-gray-900/25 pt-32 sm:pt-48 lg:pt-64">\n                        <div class="p-4 sm:p-6">\n                            <time datetime="${t.date}" class="block text-xs text-white/90">${new Date(t.date).toLocaleDateString()}</time>\n                            <a href="/blog/index.html?post=${t.slug}">\n                                <h3 class="mt-0.5 text-lg text-white">${t.title}</h3>\n                            </a>\n                            <p class="mt-2 line-clamp-3 text-sm/relaxed text-white/95">\n                                ${t.description}\n                            </p>\n                        </div>\n                    </div>\n                `,e.appendChild(n)}))}))}));