function toggleModal(e){document.getElementById("supportModal").classList.toggle("hidden",!e)}function submitForm(e){e.preventDefault();const t=document.getElementById("kecamatan"),n=t.options[t.selectedIndex].textContent,o=new FormData(document.getElementById("supportForm"));let r=Object.fromEntries(o.entries());r.kecamatan=n,fetch("https://inimibarru.com/landing-api/join",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(r)}).then((e=>e.json())).then((e=>{const t=document.getElementById("response");e.success?(t.innerText="Dukungan berhasil dikirim!",t.style.color="green",toggleModal(!1)):(t.innerText=`Gagal: ${e.error}`,t.style.color="red")})).catch((e=>{const t=document.getElementById("response");t.innerText="Terjadi kesalahan. Coba lagi.",t.style.color="red",console.error("Error:",e)}))}